svg.script:
  window.onload=start;

  var centerX = 300; 
  var centerY = 300; 

  var ns ="http://www.w3.org/2000/svg"; 

  function start() { 

     var circles = [ { radius:50, fillcolor:"blue" }, { radius:70, fillcolor:"lightblue" }, { radius:90, fillcolor:"green" }, { radius:120, fillcolor:"yellow" }, { radius:150, fillcolor:"blue" } ] ;

	for ( obj in circles ) { 
  		a = new circle( obj.radius, obj.fillcolor); 
 	} 	

  } 

  function circle(radius, fillcolor) { 
 	var cElement = document.createElementNS( ns, "circle"); 
        cElement.setAttributeNS(ns, "cx",centerX); 
        cElement.setAttributeNS(ns, "cy",centerY); 
        cElement.setAttributeNS(ns, "r", radius);
        cElement.setAttributeNS(ns, "stroke-width", "0");
        cElement.setAttributeNS(ns, "fill" , fillcolor );
 	document.getElementById("container").appendChild(cElement);

  }
